$(".menu__link").mouseover(function () {
    $(".menu__link").removeClass("_active")
    // $(".menu__links").css("min-height","unset")
    if($(this).hasClass("_toggle-submenu")){
        let fontSize = $(".page").css("font-size")
        let fontSizeNum = fontSize.substring(0, fontSize.length - 2)
        let parent = $(this).parents(".submenu")
        let height = $(parent).find(".menu__links2").height()
        height = height / fontSizeNum
        // $(this).parents(".menu__grid").find(".menu__links").css("min-height",`${height}em`)
        $(this).addClass("_active")
    }
})


function showMenu() {
    $(".header").addClass("_open")
    $("body").addClass("fixed-body")
}

function hideMenu() {
    $(".header").removeClass("_open")
    $(".header").removeClass("_menu-open")
    $(".header").removeClass("_search-open")
    $("body").removeClass("fixed-body")
}

